/*@ define:input:global_defaults
type: stringlist
label: Items to hide
group: Item Filters
*/
#define VAR_GLOBAL_HIDE []

/*@ define:input:global_defaults
type: stringlist
label: Items to show
group: Item Filters
*/
#define VAR_GLOBAL_SHOW []


/*@ define:input:global_defaults
type: boolean
label: Show Icons on all items
group: Icon Settings
*/
#define VAR_GLOBAL_SHOW_ICONS true

/*@ define:input:global_defaults
type: stringlist
label: Items to show icons for (if not showing for all items)
group: Icon Settings
*/
